import React from "react";
import { RouteComponentProps } from "react-router-dom";

const About = (props: RouteComponentProps) => {
	// component wrapped by Route, Route adds info to the component's props
	console.log(props);

	// programatically redict user to a different URL after 2s
	setTimeout(() => {
		props.history.push("/home");
	}, 2000);

	return (
		<div className="about-container">
			<h4>About</h4>
		</div>
	);
};

export default About;

// alternatively for wrapping the component in <Route>
//  we can also apply high order component withRouter()
// export default withRoute(About);
